import React from 'react'
import Slider from "react-slick";
import Data from './Data'

const productItems=Data.productItems


//next Slide
const nextArrow=(props)=>{
  const {onClick}=props
  return(
    <div className='nexArrow' onClick={onClick}>
      <button className="next">
      <i class="fa-solid fa-chevrons-right"></i>
      </button>
    </div>
  )}


//next Slide
const prevtArrow=(props)=>{
  const {onClick}=props
  return(
    <div className='prevArrow' onClick={onClick}>
      <button className="prev">
      <i class="fa-solid fa-chevrons-right"></i>
      </button>
    </div>
  )}




const SlideCard = () => {
  var settings = {
        //dots: true,
        infinite: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 2000,
        pauseOnHover: true,
        nextArrow:<nextArrow/>,
        prevArrow:<prevArrow/>,
        }    
  return (
    <>
    <div className="Cards">
      <div className='container mx-auto'>
      <Slider {...settings}>
      {
        productItems.map((path,index)=>{
          return(
              <div className="Card shadow-xl" key={index}>
                <div className="CardImg">
                  <img src={path.cover} alt="" />
                  <span className="discount">{path.discount}% OFF</span>
                  <span className="availabe">Sold Out</span>
                </div>
                <div className="CardDetails">
                  <h2>{path.name}</h2>
                  <div className="div CardRate">
                  <i class="fa-solid fa-star"></i>
                  <i class="fa-solid fa-star"></i>
                  <i class="fa-solid fa-star"></i>
                  <i class="fa-solid fa-star"></i>
                  <i class="fa-solid fa-star"></i>
                  </div>
                  <div className="price">
                    <h4>{path.price}$</h4>
                  </div>
                </div>
            </div>
          )})
        }
        </Slider>
        </div>
      </div>
    </>
  )
}

export default SlideCard
